import React, { FC, useState } from "react";
import useForm from "../hooks/useForm";
import { Wrapper, FlexForm, Input } from "../styles/main";
import { useCreateLoginAuthMutation } from "../generated/graphql";

const initialValues = {
  email: "",
  magicword: ""
};

const Login: FC<any> = () => {
  const [values, handleChange] = useForm(initialValues);
  const [tokenSent, setTokenSent] = useState(false);
  const [createLoginAuth] = useCreateLoginAuthMutation();

  async function handleGenerateAuth(values) {
    event.preventDefault();
    try {
      const res = await createLoginAuth({
        variables: {
          email: values.email
        }
      });
      console.log(res);
      setTokenSent(true);
    } catch (err) {
      //fail gracefully
    }
  }

  function handleLogin(values) {
    console.log("login");
  }

  return (
    <Wrapper>
      <h3> Login</h3>
      {!tokenSent ? (
        <FlexForm onSubmit={() => handleGenerateAuth(values)}>
          <Input
            type="text"
            placeholder="email"
            name="email"
            value={values.email}
            onChange={handleChange}
          />
          <button type="submit">Get Token</button>
        </FlexForm>
      ) : (
        <FlexForm onSubmit={() => handleLogin(values)}>
          <Input
            type="text"
            placeholder="magic word"
            name="username"
            value={values.magicword}
            onChange={handleChange}
          />
          <button type="submit">log in</button>
        </FlexForm>
      )}
    </Wrapper>
  );
};

export default Login;
