import { keyframes, css, Global } from "@emotion/react";
import styled from "@emotion/styled";

export const globalStyles = (
  <Global
    styles={css`
      html,
      body {
        padding: 3rem 1rem;
        margin: 0;
        background: papayawhip;
        min-height: 100%;
        font-family: Helvetica, Arial, sans-serif;
        font-size: 24px;
      }
    `}
  />
);

const row = css`
  display: flex;
  flex-flow: row;
`;
const column = css`
  display: flex;
  flex-flow: column;
`;

export const flex = {
  row,
  column
};
