import { keyframes, css, Global } from "@emotion/react";
import styled from "@emotion/styled";

export const globalStyles = (
  <Global
    styles={css`
      html,
      body {
        display: flex;
        flex-flow: column;
        align-items: center;
        padding: 0;
        margin: 0;
        min-height: 100%;
        font-family: Helvetica, Arial, sans-serif;
        font-size: 24px;
      }
    `}
  />
);

const row = css`
  display: flex;
  flex-flow: row;
`;
const column = css`
  display: flex;
  flex-direction: column;
`;

export const flex = {
  row,
  column
};

const wrapStyles = css`
  padding: 40px 80px;
  border-top: solid 1px rgba(0, 0, 0, 0.1);
  border-bottom: solid 1px rgba(0, 0, 0, 0.1);
`;

export const Wrapper = styled("div")`
  ${wrapStyles}
`;

export const FlexForm = props => {
  if (props.column) {
    return <form {...props} css={flex.column} />;
  }
};
